import React, { useState, useEffect } from 'react';
import './styles.css';

function AddProduct() {
  const [showModal, setShowModal] = useState(false);

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      <button type="button" className="btn btn-primary" onClick={handleShowModal}>
        <i className="bi bi-plus"></i> Thêm mới sản phẩm
      </button>
      
      {showModal && (
        <div className="modal fade" id="myModal" role="dialog">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h4 className="modal-title" style={{ color: 'blue' }} id="headAddProduct">
                  Thêm mới sản phẩm
                </h4>
                <h4 className="modal-title" style={{ display: 'none', color: 'rgb(149, 149, 0)' }} id="headEditProduct">
                  Chỉnh sửa sản phẩm
                </h4>
                <button type="button" className="close" data-dismiss="modal" onClick={handleCloseModal}>
                  &times;
                </button>
              </div>
              <div className="modal-body">
                <label>Tên Sản Phẩm</label>
                <input type="text" className="form-control" id="nameProduct" placeholder="Tên sản Phẩm" />
                <label>Số lượng</label>
                <input type="text" className="form-control" id="numProduct" placeholder="Số lượng" />
                <label>Giá sản phẩm</label>
                <input type="text" className="form-control" id="priceProduct" placeholder="Số lượng" />
                <label>Link sản phẩm</label>
                <input type="text" className="form-control" id="LinkProduct" placeholder="Link hình ảnh" />
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-dismiss="modal" onClick={handleCloseModal}>
                  Close
                </button>
                <button type="button" className="btn btn-primary" id="saveBtn">
                  Save
                </button>
                <button type="button" className="btn btn-warning" id="editBtn" disabled>
                  Update
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AddProduct;
