import React, { useState } from 'react';
import { Button, Modal } from 'react-bootstrap';
import './css/headMenu.css'
import 'bootstrap/dist/css/bootstrap.min.css';

function AddProduct() {
  const [showModal, setShowModal] = useState(false);

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const addButton = {
  id: 'idAddButon',
  // Các thuộc tính khác
};

  return (
    <div>
      <Button id={addButton.id} variant="primary" onClick={handleShowModal}>
        <i className="bi bi-plus"></i> Thêm mới sản phẩm
      </Button>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Thêm mới sản phẩm</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <label>Tên Sản Phẩm</label>
                <input type="text" className="form-control" id="nameProduct" placeholder="Tên sản Phẩm" />
                <label>Số lượng</label>
                <input type="text" className="form-control" id="numProduct" placeholder="Số lượng" />
                <label>Giá sản phẩm</label>
                <input type="text" className="form-control" id="priceProduct" placeholder="Số lượng" />
                <label>Link sản phẩm</label>
                <input type="text" className="form-control" id="LinkProduct" placeholder="Link hình ảnh" />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Đóng
          </Button>
          <Button variant="primary">
            Lưu Thay Đổi
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default AddProduct;
