import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Card, Row, Col } from 'react-bootstrap';

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get('https://65248470ea560a22a4e9e5cc.mockapi.io/api/v1/product')
      .then(response => {
        setProducts(response.data.products);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <Container>
      <Row>
        {products.map(item => (
          <Col key={item.id} xs={6} md={2}>
            <Card className="mb-4">
              <Card.Img src={item.image} alt="Image" />
              <Card.Body>
                <Card.Title>{item.name}</Card.Title>
                <Card.Text style={{ color: 'red' }}>{item.price}</Card.Text>
                <button className="btn btn-sm btn-warning editButton" data-id={item.id} data-toggle="modal" data-target="#myModal">Edit</button>
                <button className="btn btn-sm btn-danger deleteButton" data-id={item.id} style={{ float: 'right' }}>Delete</button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default ProductList;
